import { useState, createRef } from 'react';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import Dropdown from 'react-bootstrap/Dropdown';
import DropdownButton from 'react-bootstrap/DropdownButton';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';

let exampleData = [
  {
    title: 'topic one',
    timeEstimate: 5,
    textDescription: 'this is topic 1 description',
  },
  {
    title: 'topic2',
    timeEstimate: 10,
    textDescription: 'this is topic 2 description',
  },
  {
    title: 'topic3',
    timeEstimate: 15,
    textDescription: 'this is topic 3 description blabdlabdlbwa ahwjkdh awjhd awjwdh hwajk dwja dhwja dwhaj dawj',
  },
];

function App() {
  const [agendaData, setAgendaData] = useState(exampleData);
  const [showEdit, setShowEdit] = useState(false);
  const [curElm, setCurElm] = useState({ title: 'new topic', timeEstimate: 'time required', textDescription: 'description', elmIndex: undefined });

  const editDataModal = (elm, index) => {
    let newData = { title: undefined, timeEstimate: undefined, textDescription: undefined };
    return (
      <div>
        <Form id='modify-modal__container'>
          <Form.Group>
            <Form.Label>Meeting Topic</Form.Label>
            <Form.Control type='text' placeholder={elm.title} onChange={(e) => (newData.title = e.target.value)} />
          </Form.Group>

          <Form.Group>
            <Form.Label>Time Estimate</Form.Label>
            <Form.Control type='text' placeholder={elm.timeEstimate} onChange={(e) => (newData.timeEstimate = e.target.value)} />
          </Form.Group>

          <Form.Group>
            <Form.Label>Text Description</Form.Label>
            <Form.Control type='text' placeholder={elm.textDescription} onChange={(e) => (newData.textDescription = e.target.value)} />
          </Form.Group>

          <Button variant='success' type='submit' style={{ backgroundColor: '#5a935ab7' }} onClick={() => {}}>
            Submit
          </Button>

          <Button
            variant='secondary'
            type='reset'
            style={{ marginLeft: '1vmin' }}
            onClick={() => {
              console.log(newData);
            }}
          >
            Cancel
          </Button>
        </Form>
      </div>
    );
  };

  return (
    <div id='App'>
      {showEdit && editDataModal(curElm, undefined, undefined)}

      <div id='agenda__container'>
        <h2>Agenda Plan</h2>
        {agendaData.map((elm, index) => {
          return (
            <div id={elm.title + index} key={elm.title + index}>
              <div className='agenda-topic__container'>
                <h4 style={{ gridColumnStart: 'col1' }}>{elm.title}</h4>
                <p style={{ textAlign: 'center', color: '#5a585C' }}>{elm.timeEstimate + 'mins'}</p>
                <p>{elm.textDescription}</p>
                {/* <button type="button" class="btn btn-light btn-sm">Edit</button> */}

                <Form style={{ paddingTop: '40%' }}>
                  <Form.Check type='checkbox' label='' />
                </Form>

                <DropdownButton title='' drop='end' size='sm' id='dropdown' variant='Secondary'>
                  <Dropdown.Item
                    eventKey='1'
                    onClick={() => {
                      setCurElm(elm, setAgendaData, index);
                      setShowEdit(true);
                    }}
                  >
                    Modify
                  </Dropdown.Item>
                  <Dropdown.Item
                    eventKey='2'
                    onClick={() => {
                      document.getElementById(elm.title + index).setAttribute('style', 'display:none');
                    }}
                  >
                    Delete
                  </Dropdown.Item>
                </DropdownButton>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default App;
